mapper.select:


    -- 忽略管理员账户
    t.user_id != 1
     and t.sex = #{sex}
     and t.status = #{status}
     and t.create_time between #{params.beginCreateTime} and #{params.endCreateTime}
     and t.real_name like concat('%', #{realName}, '%')
     and t.major_id = #{majorId}
     and t.grade_id = #{gradeId}
     and FIND_IN_SET(t2.role_id, #{roleIds})  -- 主要用于筛选角色

GROUP BY
    t.user_id
order by t.create_time desc

mapper.update:
update sys_user
set status = #{status}
where user_id = #{userId}

update sys_user
set password = #{password}
where user_id in

        #{userId}
